import React, { useState } from 'react';
import './FuelQuoteForm.css'; // Import your CSS file

function FuelQuoteForm() {
  const [clientName, setClientName] = useState('');
  const [amount, setAmount] = useState(0);
  const [price, setPrice] = useState(0.0);
  const [inState, setInState] = useState(true);
  const [previousClient, setPreviousClient] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // You can handle form submission logic here
    // For example, you can send the form data to a server or perform client-side validation.
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Client Name:
            <input
              type="text"
              placeholder="Client Name"
              value={clientName}
              onChange={(e) => setClientName(e.target.value)}
            />
          </label>
        </div>

        <div>
          <label>
            Amount or Number:
            <input
              type="number"
              placeholder="Amount or Number"
              value={amount}
              onChange={(e) => setAmount(parseInt(e.target.value))}
            />
          </label>
        </div>

        <div>
          <label>
            Price:
            <input
              type="number"
              step="0.01"
              placeholder="Price"
              value={price}
              onChange={(e) => setPrice(parseFloat(e.target.value))}
            />
          </label>
        </div>

        <div>
          <label>
            In State:
            <select value={inState} onChange={(e) => setInState(e.target.value === 'true')}>
              <option value="true">True</option>
              <option value="false">False</option>
            </select>
          </label>
        </div>

        <div>
          <label>
            Previous Client:
            <select value={previousClient} onChange={(e) => setPreviousClient(e.target.value === 'true')}>
              <option value="true">True</option>
              <option value="false">False</option>
            </select>
          </label>
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default FuelQuoteForm;
